import React from 'react'
import { useState,  } from 'react';
import {  Link } from 'react-router-dom'
import "./AddEdit.css" 
import axios  from 'axios';
import {toast} from 'react-toastify'

const initialState = {
    name: "",
    email: "",
    contact: "", 
    age: "", 
    batch:"" , 
    state: " "
}; 
const AddEdit = () => {
    const [state, setState] = useState(initialState);

    let { name, email, contact ,age,batch} = state;



    const handleSubmit = (e) => {
        e.preventDefault();
        if (!name || !email || !contact|| !age ||batch ) {
            toast.error("Please enter all fileld");
        }
        else if (age <= 18 || age >= 65) {
            toast.error("Your age is not eligible")
        }
        else {
            batch = 'D';
            axios.post("http://localhost:5000/api/post", {
                name,
                email,
                contact, 
                age,
                batch
            }).then(() => {
                setState({ name: " ", email: " ", contact: " "  ,age:" " , batch:" "})
            }).catch((err) => {
                toast.error(err.response.data)
            })

            setTimeout(() => toast.success("Contact added sucessfully"), 500);
        }
    }


    const handleInput = (e) => {
        const { name, value } = e.target 
        setState({ ...state, [name]: value })
}

  return (
      <div style={{ marginTop: "50px"}}>
          <form style={{
              margin: "auto", 
              padding: "15px", 
              maxWidth: "400px", 
              alignContent:"center"
          }}
              onSubmit={handleSubmit}>
              <label htmlFor='name'> Name</label>
              <input type="text" id="name" name="name" placeholder="Enter Your Name" value={name } onChange={handleInput}/>

              <label htmlFor='email'> Email</label>
              <input type="email" id="email" name="email" placeholder="Enter Your Email" value={email } onChange={handleInput}/>


              <label htmlFor='Contact'> Contact</label>
              <input type="contact" id="contact" name="contact" placeholder="Enter Your contact" value={contact} onChange={handleInput}/>


              <label htmlFor='Age'> Age</label>
              <h5>Age Must be in 18 to 65</h5>
              <input type="age" id="age" name="age" placeholder="Enter Your age" value={age} onChange={handleInput} />

          <label htmlFor='Batch'> Batch</label>

              <select name="batch" id="batch" form="batches">
                  <option value={ batch}>6AM - 7AM</option>
                  <option value={batch}>8AM- 9AM</option>
              <option value={batch}>9AM-10AM</option>
                  <option value={batch}>5PM - 6PM</option>
              </select>

              <input type="submit" value="Save" />
              <Link to='/'>
                <input type="button" value="Go back"/>
              </Link>
          </form>

    </div>

  )
}

export default AddEdit;
