import React from 'react'
import { useState, useEffect } from 'react';
import { useNavigate, useParams, Link } from 'react-router-dom'
import "./AddEdit.css" 
import axios  from 'axios';
import {toast} from 'react-toastify'

const initialState = {
    name: "",
    email: "",
    state: " "
}; 
const AddEdit = () => {
    const [state, setState] = useState(initialState);
    const history = useNavigate()

    const { name, email, contact } = state;

    const { id } = useParams(); 

    useEffect(() => {
        axios.get(`http://localhost:5000/api/remove/${id}`).then((res)=> setState({...res.data[0]}))
    } , [id])

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!name || !email || !contact) {
            toast.error("Please enter all fileld");
        } else {
            axios.post("http://localhost:5000/api/post", {
                name,
                email,
                contact
            }).then(() => {
                setState({ name: " ", email: " ", contact: " " })
            }).catch((err) => {
                toast.error(err.response.data)
            })

            setTimeout(() => toast.success("Contact added sucessfully"), 500);
        }
    }


    const handleInput = (e) => {
        const { name, value } = e.target 
        setState({ ...state, [name]: value })
}

  return (
      <div style={{ marginTop: "100px"}}>
          <form style={{
              margin: "auto", 
              padding: "15px", 
              maxWidth: "400px", 
              alignContent:"center"
          }}
              onSubmit={handleSubmit}>
              <label htmlFor='name'> Name</label>
              <input type="text" id="name" name="name" placeholder="Enter Your Name" value={name ||" "} onChange={handleInput}/>

              <label htmlFor='email'> Email</label>
              <input type="email" id="email" name="email" placeholder="Enter Your Email" value={email || " "} onChange={handleInput}/>


              <label htmlFor='Contact'> Contact</label>
              <input type="contact" id="contact" name="contact" placeholder="Enter Your contact" value={contact} onChange={handleInput}/>

              <input type="submit" value="Save" />
              <Link to='/'>
                <input type="button" value="Go back"/>
              </Link>
          </form>

    </div>

  )
}

export default AddEdit;
