    name: "",
    email: "",
    state: " "
}; 
const AddEdit = () => {
    const [state, setState] = useState[initialState]; 

    const [name, email, contact] = state; 

    const handleSubmit = (e) => {
        e.preventDefault(); 
    }
  return (
      <div style={{ marginTop: "100px"}}>
          <form style={{
              margin: "auto", 
              padding: "15px", 
              maxWidth: "400px", 
              alignContent:"center"
          }}
              onSubmit={handleSubmit}>
              <label htmlFor='name'> Name</label>
              <input type="text" id="name" name="name" placeholder="Enter Your Name" value={name} onChange={handleInput}></input>

              <label htmlFor='email'> email</label>
              <input type="email" id="email" name="email" placeholder="Enter Your Email" value={email} onChange={handleInput}></input>


              <label htmlFor='Contact'> Contact</label>
              <input type="number" id="contact" name="contact" placeholder="Enter Your contact" value={contact} onChange={handleInput}></input>

          </form>
    </div>
  )
}

export default AddEdit;
